import numpy as np
import pandas as pd

a = 6378137
f = 1/298.257222101
e = np.sqrt(2*f-f**2)
k = a*(1-e**2)
n = 50 #escolha um número par

def f(x):
  return k*(1-(e**2)*((np.sin(x))**2))**(-1.5)

def sm(a, b): #simpson method
    #Calcula a integral de f(x) de a a b usando o método de Simpson com n subintervalos.


    h = (b - a) / n
    x = [a + i * h for i in range(n + 1)]
    y = [f(x_i) for x_i in x]

    integral = y[0] + y[-1]  # Termos extremos

    # Termos pares
    integral += sum([4 * y[i] for i in range(1, n, 2)])

    # Termos ímpares
    integral += sum([2 * y[i] for i in range(2, n-1, 2)])

    integral *= h / 3

    return integral

valores_ang = np.array([0, 15, 30, 45, 60, 75, 90])
valores_phi = valores_ang*np.pi/180
valores_ang2 = np.array([1, 1/60, 1/3600])
valores_phi2 = valores_ang2*np.pi/180

# Cria uma matriz para armazenar os valores da função
matriz_valores = np.zeros((len(valores_phi), len(valores_phi2)))

# Preenchimento da matriz com os valores da função para cada combinação de phi e lam
for i, phi in enumerate(valores_phi):
    for j, delta_phi in enumerate(valores_phi2):
        matriz_valores[i, j] = sm(phi, phi + delta_phi)

# Dataframe
tabela = pd.DataFrame(matriz_valores, index=valores_ang, columns=['1°','1\'','1\"'])

print(tabela)

"""Cálculo do comprimento de arco de Paralelo"""

import numpy as np
import pandas as pd

a = 6378137
f = 1/298.257222101
e = np.sqrt(2*f-f**2)

def N(phi):
  return a/np.sqrt(1-(e**2)*(np.sin(phi)**2))

def l(phi, lam):
  return N(phi)*np.cos(phi)*lam

valores_ang = np.array([0, 15, 30, 45, 60, 75, 90])
valores_phi = valores_ang*np.pi/180
valores_ang2 = np.array([1, 1/60, 1/3600])
valores_lam = valores_ang2*np.pi/180

# Cria uma matriz para armazenar os valores da função
matriz_valores = np.zeros((len(valores_phi), len(valores_lam)))

# Preenchimento da matriz com os valores da função para cada combinação de phi e lam
for i, x in enumerate(valores_phi):
    for j, y in enumerate(valores_lam):
        matriz_valores[i, j] = l(x, y)

# Dataframe
tabela = pd.DataFrame(matriz_valores, index=valores_ang, columns=['1°','1\'','1\"'])

print(tabela)